import "./About.css";
import img from "../images/about-img.svg";
import WhyJoinUs from "../WhyJoinUs/WhyJoinUs";
const About = () => {
  const [whylearings , setLearning] = useState([]);
 

  useEffect( ()=>{
    fetch('./whyjoinus.json')
    .then(res => res.json())
    .then(data => setLearning(data))
  },[])
 
  return (
    <div className="container ">
      <h1 className="text-center about-us"> WHY JOIN LEARN</h1>

      <hr className="w-25 m-auto text-color " />
     
    {
      whylearings.map(whylearing => <WhyJoinUs
      
      ></WhyJoinUs>)
    }
      </div>
     
 
  );
};

export default About;
