import React, { useEffect, useState } from "react";

import "./About.css";
import img from "../images/about-img.svg";
const About = () => {
  const [whylearing , setLearning] = useState([]);
  const [count, setCount] = useState()

  useEffect( ()=>{
    fetch('./whyjoinus.json')
    .then(res => res.json())
    .then(data => setLearning(data))
  })
 
  return (
    <div className="container ">
      <h1 className="text-center about-us"> WHY JOIN LEARN</h1>

      <hr className="w-25 m-auto text-color " />
      <div className="row d-flex align-items-center mb-5">
        <div className="col-lg-6 col-md-6 col-12">
        </div>

        <div className="col-lg-6 col-md-6 col-12 ">
          <img className="img-fluid p-5" src={img} alt="img" />;
        </div>
      </div>
      <button onClick={(e) => setCount(count + 1)}>Incress</button>
    </div>
  );
};

export default About;
