import {
  getAuth,
  signInWithPopup,
  GoogleAuthProvider,
  FacebookAuthProvider,
  signOut,
  createUserWithEmailAndPassword ,
  onAuthStateChanged,
} from "firebase/auth";
import { useEffect, useState } from "react";
import initilizeauthentication from "../Firebase/firebase.initialize";
initilizeauthentication();

const auth = getAuth();

export const [email, setEmail] = useState("");
export const [password, setPassword] = useState("");
const useFirebase = () => {
  const [users, setUsers] = useState({});

  // Singin useing Google

  const signinUsingGoogle = () => {
    const googleProvider = new GoogleAuthProvider();
    signInWithPopup(auth, googleProvider)
      .then((result) => {
        const { displayName, email, photoURL } = result.user;
        console.log(result.user);
        const logedInuser = {
          name: displayName,
          email: email,
          photo: photoURL,
        };
        setUsers(logedInuser);
      })
      .catch((error) => {});
  };

  // Singin useing Facebook

  const FacebookSignin = () => {
    const facebookprovider = new FacebookAuthProvider();
    signInWithPopup(auth, facebookprovider)
      .then((result) => {
        const { displayName, email, photoURL, uid } = result.user;
        console.log(result.user);

        const logeduser = {
          name: displayName,
          email: email,
          photo: photoURL,
        };

        setUsers(logeduser);
      })

      .catch((error) => {});
  };

  //  Sign in useing email and passWord

 

  const hadleRegistration = (e) => {
    e.preventDefault();
    if (password.length < 6) {
      return;
    }
    createUserWithEmailAndPassword(auth, email, password)
      .then((result) => {
        const { email, displayName } = result.user;
        const logeduser = {
          name: displayName,
          email: email,
        };
        setUser(logeduser);
      })
      .catch((err) => {
        console.log("Error", err);
      });
  };

  // showing data all tabs
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setUsers(user);
      } else {
        setUsers({});
      }
    });
    return () => unsubscribe;
  }, []);
  const logOut = () => {
    signOut(auth).then(() => {});
  };

  return {
    users,
    signinUsingGoogle,
    FacebookSignin,
    hadleRegistration,
    logOut,
  };
};

export default useFirebase;
